import {React, useState, useEffect} from 'react'
import {ethers} from 'ethers'
// import styles from './NavBar.module.css'




const MenuItem = (props) => {
    let text = props.text;
    return <button onClick={() => {window.alert({text})}} className=' px-2 flex justify-end'>{text}</button>
}

const NavBar = () => {
    const [menuOpen, setMenuOpen] = useState('none');

    const openMenu = () => {
        console.log('working');
        if (menuOpen == null || menuOpen == 'none'){
            setMenuOpen('flex');
        } else {
            setMenuOpen('none')
        }
    }

    const handleResize = () => {
        if (window.innerWidth < 760){
            setMenuOpen('none');
        } else {
            setMenuOpen('flex');
        }
    }
    window.addEventListener("resize", handleResize);
    window.addEventListener("load", handleResize);

    useEffect(() => {

    })

    var myMenu = ['Home','Settings','About us', 'Other stuff'];
    return (
        <div className='flex justify-end py-10'>
          <button onClick={() => {openMenu()}} className=' hamburger flex justify-end'>m</button>
          <ul className=' flex justify-end py-10' style={{'display':menuOpen}}>
            {myMenu.map(item => {
              return <MenuItem key={item} text={item}/>
            })}
          </ul>
          <button onClick={() => {openMenu()}} className=''>close</button>
        </div>    
      );
}

export default NavBar;
