import {React, useState, useEffect} from 'react'
import {ethers} from 'ethers'
// import styles from './NavBar.module.css'




const MenuItem = (props) => {
    let text = props.text;
    return <button onClick={() => {window.alert({text})}} className=' justify-end'>{text}</button>
}

const NavBar = () => {
    const [menuOpen, setMenuOpen] = useState('none');
    const [menuTransform, setMenuTransform] = useState('translateX(0%)')

    const openMenu = () => {
        console.log('working');
        // setMenuTransform('translateX(100%)');
        if (window.innerWidth < 760){



            if (menuTransform == null || menuTransform == 'translateX(0)'){

                setMenuTransform('translateX(-120%)');

            } else {
                setMenuTransform('translateX(0)')
            }
        } else {
            setMenuOpen('none');
        }


    }

    const handleResize = () => {
        if (window.innerWidth < 760){
            setMenuOpen('flex');
            setMenuTransform('translateX(-120%)');
        } else {
            setMenuOpen('flex');
            setMenuTransform('translateX(0)')
        }
    }
    window.addEventListener("resize", handleResize);
    window.addEventListener("load", handleResize);

    useEffect(() => {

    })

    var myMenu = ['Home','Settings','About us', 'Other stuff'];
    return (
        <div className='flex justify-start p-2 '>
          <button onClick={() => {openMenu()}} className='navButton' style={{'transform':menuTransform, 'display': menuOpen}}>X</button>
          <div className='bg-indigo-300 wrapper' style={{'transform':menuTransform, 'display': menuOpen}}>

            <ul className=' justify-start bg-indigo-300' style={{'display': 'flex'}}>

                {myMenu.map(item => {
                return <MenuItem key={item} text={item}/>
                })}
            </ul>

          </div>


        </div>    
      );
}

export default NavBar;
