import {React, useState, useEffect} from 'react'
import {ethers} from 'ethers'
// import styles from './NavBar.module.css'




const MenuItem = (props) => {
    let text = props.text;
    return <button onClick={() => {window.alert({text})}} className=' justify-end'>{text}</button>
}

const NavBar = () => {
    const [menuOpen, setMenuOpen] = useState('translateX(0)');

    const openMenu = () => {
        console.log('working');
        if (menuOpen == null || menuOpen == 'none'){
            setMenuOpen('flex');
        } else {
            setMenuOpen('none')
        }
    }

    const handleResize = () => {
        if (window.innerWidth < 760){
            setMenuOpen('translateX(0)');
        } else {
            setMenuOpen('translateX(-100%)');
        }
    }
    window.addEventListener("resize", handleResize);
    window.addEventListener("load", handleResize);

    useEffect(() => {

    })

    var myMenu = ['Home','Settings','About us', 'Other stuff'];
    return (
        <div className='flex justify-end p-2 '>
          <button onClick={() => {openMenu()}} className=' hamburger flex justify-end'>m</button>
          <div className='bg-indigo-300 wrapper'>
            <ul className=' justify-end bg-indigo-300' style={{'transform':menuOpen}}>
                {myMenu.map(item => {
                return <MenuItem key={item} text={item}/>
                })}
            </ul>
          </div>

          <button onClick={() => {openMenu()}} className=''>X</button>
        </div>    
      );
}

export default NavBar;
