import {React, useState, useEffect} from 'react'
import {ethers} from 'ethers'

const Interactions = (props) => {

    const [txnHash, setTxnHash] = useState();

    const [allowance, setAllowance] = useState();
    const [selfAllowance, setSelfAllowance] = useState();
    const [ApproveOrStake, setApproveOrStake] = useState();
    const [stakeBalance, setStakeBalance] = useState();

    let contract = props.contract.contract;
    let defaultAccount = props.contract.defaultAccount;


    const approveOrStake = async () => {
        let contractAddress = await contract.address;
        let allowance = await contract.allowance(defaultAccount, contractAddress);

        let allowanceBalance = allowance * Math.pow(10, 18);
        setAllowance(allowanceBalance);

        let selfAllowance = await contract.allowance(defaultAccount, defaultAccount);
        let selfAllowanceBalance = selfAllowance * Math.pow(10, 18);
        setSelfAllowance(selfAllowanceBalance);

        if (allowanceBalance > 0 && selfAllowanceBalance > 0){
            setApproveOrStake("Stake");
        } else {
            setApproveOrStake("Approve");
        }
    }

    const getStakeBalance = async () =>{
        let [isStakeHolder, stakeId] = await contract.isStakeholder(defaultAccount);

        if (isStakeHolder == true){
            let stakeAmountBig = await contract.stakedTokens(defaultAccount);
            let stakeAmount = stakeAmountBig;
            setStakeBalance(stakeAmount);
        } else {
            setStakeBalance(0);
        }
    }

    const stakeHandler = async (e) => {
        e.preventDefault();
        let contractAddress = await contract.address;
        // let allowance = await contract.allowance(defaultAccount, contractAddress);
        // let allowanceBalance = allowance / Math.pow(10, 18);


        //let stakeAmount = parseInt(e.target.stakeAmount.value) * Math.pow(10, 18);
        //let stakeAmountBig = ethers.BigNumber.from(e.target.stakeAmount.value);
        //let stakeAmount = stakeAmountBig.mul(Math.pow(10, 18));
        let stakeAmount = parseInt(e.target.stakeAmount.value);
        let stakeAmountBig = ethers.utils.parseUnits(e.target.stakeAmount.value, 18);
        // let allowanceBig = ethers.utils.parseUnits(allowance.toString());
        console.log(allowance,selfAllowance, parseInt(ethers.utils.formatUnits(stakeAmountBig,0)))

        if (parseInt(ethers.utils.formatUnits(stakeAmountBig,0)) <= allowance && parseInt(ethers.utils.formatUnits(stakeAmountBig,0)) <= selfAllowance){

            let txn = await contract.stakeToken(stakeAmountBig);
            console.log(txn);
		    setTxnHash("Stake transaction confirmation hash: " + txn.hash);

        } else {
            let approveSelftxn = await contract.approve(defaultAccount, stakeAmountBig);
            let txn = await contract.approve(contractAddress, stakeAmountBig);
            console.log(txn);
		    setTxnHash("Approval transaction confirmation hash: " + txn.hash);
        }
    }

    useEffect(() =>{
        if (contract != null){
            approveOrStake();
            getStakeBalance();
        } else {
            setApproveOrStake("Approve");
        }
    },[contract])

    return (
        <div>
            <form onSubmit={stakeHandler}>
                <h3>Stake tokens </h3>
                <p>Amount</p>
                <input type='number' id='stakeAmount' min='0' step='1'></input>
                <button type='submit'>{ApproveOrStake}</button>
            </form>
            <div>
                <h3>Staked balance: {stakeBalance}</h3>
            </div>
            <div>
				{txnHash}
			</div>
        </div>

    );

}

export default Interactions;
